import { bytesToHex, encodeFunctionData, type Abi } from "viem";
import {
  Faucet,
  LocalECDSAKeySigner,
  HttpTransport,
  PublicClient,
  generateRandomPrivateKey,
  WalletV1,
  waitTillCompleted,
  convertEthToWei,
} from "../src";

const client = new PublicClient({
  transport: new HttpTransport({
    endpoint: "http://127.0.0.1:8529",
  }),
  shardId: 1,
});
const faucet = new Faucet(client);

const signer = new LocalECDSAKeySigner({
  privateKey: generateRandomPrivateKey(),
});

const pubkey = await signer.getPublicKey();

// biome-ignore lint/nursery/noConsole: <explanation>
console.log(`Pub key: ${pubkey}`);

const wallet = new WalletV1({
  pubkey: pubkey,
  salt: 6n,
  shardId: 1,
  client,
  signer,
});
const walletAddress = await wallet.getAddressHex();

// biome-ignore lint/nursery/noConsole: <explanation>
console.log("walletAddress", walletAddress);

const faucetHash = await faucet.withdrawToWithRetry(
  walletAddress,
  convertEthToWei(1),
);

await waitTillCompleted(client, 1, faucetHash);
await wallet.selfDeploy(true);

const { address: addressR, hash: hashR } = await wallet.deployContract({
  bytecode:
    "",
  value: 1000000n,
  gas: 100000n,
  salt: BigInt(Math.floor(Math.random() * 10000)),
  shardId: 2,
  abi: [
    {
      inputs: [
        { internalType: "address", name: "dst", type: "address" },
        { internalType: "string", name: "name", type: "string" },
      ],
      name: "orderProduct",
      outputs: [],
      stateMutability: "payable",
      type: "function",
    },
    {
      inputs: [
        { internalType: "uint256", name: "hash", type: "uint256" },
        { internalType: "bytes", name: "_authData", type: "bytes" },
      ],
      name: "verifyExternal",
      outputs: [{ internalType: "bool", name: "", type: "bool" }],
      stateMutability: "view",
      type: "function",
    },
    { stateMutability: "payable", type: "receive" },
  ] as Abi,
  args: [],
});

await waitTillCompleted(client, 1, hashR);

// biome-ignore lint/nursery/noConsole: <explanation>
console.log(`Retailer address: ${addressR}`);

const retailerWithdraw = await faucet.withdrawToWithRetry(
  addressR,
  convertEthToWei(1),
);

await waitTillCompleted(client, 1, retailerWithdraw);

const { address: addressM, hash: hashM } = await wallet.deployContract({
  bytecode:
    "",
  value: 1000000n,
  gas: 100000n,
  salt: BigInt(Math.floor(Math.random() * 10000)),
  shardId: 3,
  abi: [
    {
      inputs: [
        { internalType: "bytes", name: "pubkeyOne", type: "bytes" },
        {
          internalType: "address",
          name: "_retailerContractAddress",
          type: "address",
        },
      ],
      stateMutability: "payable",
      type: "constructor",
    },
    {
      inputs: [{ internalType: "string", name: "productName", type: "string" }],
      name: "createProduct",
      outputs: [{ internalType: "bool", name: "", type: "bool" }],
      stateMutability: "payable",
      type: "function",
    },
    {
      inputs: [],
      name: "getProducts",
      outputs: [
        { internalType: "uint256[]", name: "", type: "uint256[]" },
        { internalType: "string[]", name: "", type: "string[]" },
      ],
      stateMutability: "nonpayable",
      type: "function",
    },
    {
      inputs: [],
      name: "nextProductId",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      name: "products",
      outputs: [
        { internalType: "uint256", name: "id", type: "uint256" },
        { internalType: "string", name: "name", type: "string" },
      ],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [
        { internalType: "uint256", name: "hash", type: "uint256" },
        { internalType: "bytes", name: "signature", type: "bytes" },
      ],
      name: "verifyExternal",
      outputs: [{ internalType: "bool", name: "", type: "bool" }],
      stateMutability: "view",
      type: "function",
    },
    { stateMutability: "payable", type: "receive" },
  ] as Abi,
  args: [bytesToHex(pubkey), addressR],
});

await waitTillCompleted(client, 1, hashM);

// biome-ignore lint/nursery/noConsole: <explanation>
console.log(`Manufacturer address: ${addressM}`);

const hashFunds = await faucet.withdrawToWithRetry(
  addressR,
  convertEthToWei(1),
);

await waitTillCompleted(client, 1, hashFunds);

const hashProduct = await wallet.sendMessage({
  to: addressR,
  data: encodeFunctionData({
    abi: [
      {
        inputs: [
          { internalType: "address", name: "dst", type: "address" },
          { internalType: "string", name: "name", type: "string" },
        ],
        name: "orderProduct",
        outputs: [],
        stateMutability: "payable",
        type: "function",
      },
      {
        inputs: [
          { internalType: "uint256", name: "hash", type: "uint256" },
          { internalType: "bytes", name: "_authData", type: "bytes" },
        ],
        name: "verifyExternal",
        outputs: [{ internalType: "bool", name: "", type: "bool" }],
        stateMutability: "view",
        type: "function",
      },
      { stateMutability: "payable", type: "receive" },
    ] as Abi,
    functionName: "orderProduct",
    args: [addressM, "another-product"],
  }),
  gas: 100_000n,
  value: 1_000_000n,
});

await waitTillCompleted(client, 1, hashProduct);

// biome-ignore lint/nursery/noConsole: <explanation>
console.log("Product created!");

const hashGetProduct = await wallet.sendMessage({
  to: addressM,
  data: encodeFunctionData({
    abi: [
      {
        inputs: [
          { internalType: "bytes", name: "pubkeyOne", type: "bytes" },
          {
            internalType: "address",
            name: "_retailerContractAddress",
            type: "address",
          },
        ],
        stateMutability: "payable",
        type: "constructor",
      },
      {
        inputs: [
          { internalType: "string", name: "productName", type: "string" },
        ],
        name: "createProduct",
        outputs: [{ internalType: "bool", name: "", type: "bool" }],
        stateMutability: "payable",
        type: "function",
      },
      {
        inputs: [],
        name: "getProducts",
        outputs: [
          { internalType: "uint256[]", name: "", type: "uint256[]" },
          { internalType: "string[]", name: "", type: "string[]" },
        ],
        stateMutability: "nonpayable",
        type: "function",
      },
      {
        inputs: [],
        name: "nextProductId",
        outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
        stateMutability: "view",
        type: "function",
      },
      {
        inputs: [{ internalType: "uint256", name: "", type: "uint256" }],
        name: "products",
        outputs: [
          { internalType: "uint256", name: "id", type: "uint256" },
          { internalType: "string", name: "name", type: "string" },
        ],
        stateMutability: "view",
        type: "function",
      },
      {
        inputs: [
          { internalType: "uint256", name: "hash", type: "uint256" },
          { internalType: "bytes", name: "signature", type: "bytes" },
        ],
        name: "verifyExternal",
        outputs: [{ internalType: "bool", name: "", type: "bool" }],
        stateMutability: "view",
        type: "function",
      },
      { stateMutability: "payable", type: "receive" },
    ] as Abi,
    functionName: "getProducts",
    args: [],
  }),
  gas: 100000n,
  value: 1000000n,
});

await waitTillCompleted(client, 1, hashGetProduct);

// biome-ignore lint/nursery/noConsole: <explanation>
console.log(`Final hash: ${hashGetProduct}`);

const resultsCall = await client.call(
  {
    from: walletAddress,
    to: addressM,
    gasLimit: 100_000n,
    value: 1_000_000n,
    data: encodeFunctionData({
      abi: [
        {
          inputs: [
            { internalType: "bytes", name: "pubkeyOne", type: "bytes" },
            {
              internalType: "address",
              name: "_retailerContractAddress",
              type: "address",
            },
          ],
          stateMutability: "payable",
          type: "constructor",
        },
        {
          inputs: [
            { internalType: "string", name: "productName", type: "string" },
          ],
          name: "createProduct",
          outputs: [{ internalType: "bool", name: "", type: "bool" }],
          stateMutability: "payable",
          type: "function",
        },
        {
          inputs: [],
          name: "getProducts",
          outputs: [
            { internalType: "uint256[]", name: "", type: "uint256[]" },
            { internalType: "string[]", name: "", type: "string[]" },
          ],
          stateMutability: "nonpayable",
          type: "function",
        },
        {
          inputs: [],
          name: "nextProductId",
          outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
          stateMutability: "view",
          type: "function",
        },
        {
          inputs: [{ internalType: "uint256", name: "", type: "uint256" }],
          name: "products",
          outputs: [
            { internalType: "uint256", name: "id", type: "uint256" },
            { internalType: "string", name: "name", type: "string" },
          ],
          stateMutability: "view",
          type: "function",
        },
        {
          inputs: [
            { internalType: "uint256", name: "hash", type: "uint256" },
            { internalType: "bytes", name: "signature", type: "bytes" },
          ],
          name: "verifyExternal",
          outputs: [{ internalType: "bool", name: "", type: "bool" }],
          stateMutability: "view",
          type: "function",
        },
        { stateMutability: "payable", type: "receive" },
      ] as Abi,
      functionName: "getProducts",
      args: [],
    }),
  },
  "latest",
);

// biome-ignore lint/nursery/noConsole: <explanation>
console.log(resultsCall);
